import { useState } from 'react';
import './App.css';

function App() {
  const appel = () => {
    const arrayNumber = new Set()
    arrayNumber.add(23)
    arrayNumber.add(34)
    arrayNumber.add(45)
    arrayNumber.add(23)
    console.log(arrayNumber)
    console.log(removeDupes(arrayNumber))
  }

  const findLargestNumber = (arrayNumber) => {
    return Math.max(...arrayNumber)
  }

  const sortWords = (arrayString) => {
    return arrayString.sort()
  }

  const countOccurrences = (strings, aChar) => {
    let aCount = 0
    for(const aString of strings){
      if(aString.includes(aChar))
        aCount ++
    }
    return aCount
  }

  const flattenArray = (arrayOfArray) => {
    arrayOfArray.forEach(aArray => {
      arrayOfArray.flat()
    })
    return arrayOfArray
  }

  const removeDupes = (arrayNumber) => {
    arrayNumber.forEach(aNumber => {
      if(arrayNumber.has(aNumber)){
        arrayNumber.delete(aNumber) 
      }
    })
    return arrayNumber
  }

  return (
    <div className="App">
      <button onClick={appel}></button>
    </div>
  )
}

export default App;
