import { useState } from 'react';
import './App.css';

function App() {
  const [MaBite, setMaBite] = useState(0)

  const onClickHandler = () => {
    setMaBite((oldValue) => {
      return oldValue + 1
    })
  }

  return (
    <div className="App">
     <p>{MaBite} cm</p>
     <button onClick={onClickHandler}> ++ </button>
    </div>
  )
}

export default App;
