import './App.css';

function App() {
  let MaBite = 0

  const onClickHandler = () => {
    MaBite ++
    alert(MaBite + " cm")
  }

  return (
    <div className="App">
     <p>{MaBite}</p>
     <button onClick={onClickHandler}> ++ </button>
    </div>
  )
}

export default App;
